package week1.day1;

public class Chrome {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//** Version declaration - Data type :float **
		float version = 91.0f;
		
		//** Developer declaration - Data type :String **
		String developer = "Google";
		
		//** Beta Version declaration - Data type :boolean **
		boolean isBeta = false;
		
		//** Release Year declaration - Data type :int**
		int releaseYear = 2008;
		
		//** Shortcut Key declaration - Data type :char **
		char shortcutKey = 'C';
		
		//** This will printout the declared variables**
		System.out.println("Chrome Details are listed out below");
		System.out.println();
		System.out.println("Version : " +version);
		System.out.println("Developer : " +developer);
		System.out.println("Beta : " +isBeta);
		System.out.println("Release Year : " +releaseYear);
		System.out.println("Shortcut Key : " +shortcutKey);
			}

}
