package week1.day2;

public class FindEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int r = 10; // Range is declared as 10
			int i;
			
			System.out.println("List of Even numbers from 1 to 10 is ");
			
			for (i=0; i<=r ; i++)
			{
				//Logic to find Even
				if(i%2 == 0)
				{
						System.out.print(i + ",");
				}
					
			}

		}

}
